            } else {
                if ($('#' + error[0].id.replace('.', '\\.'))[0] !== undefined) {
                    $('#' + error[0].id.replace('.', '\\.')).remove();
                }
            }

            if ($('#error-message-' + formulario.attr('id') + ' li')[0] === undefined) {
                $('#error-message-' + formulario.attr('id') + '').hide();
            } else {
                $('#error-message-' + formulario.attr('id') + '').show();
            }
        }
    });
};

