function paginate(grid, page, pages, loadMethod, filtros) {
    $('#' + grid + ' .pagination-nav').remove();

    if (pages === undefined || pages <= 1) {
        return;
    }

    $('#' + grid).append('<nav class="pagination-nav"><ul class="pagination"></ul></nav>');
    $('#' + grid + ' .pagination').append('    <li id="' + grid + '-page-primeiro" class="disabled"><a style="cursor:pointer"'
            + ' onclick="pageClick(\'' + grid + '\', ' + 1 + ', ' + pages + ', ' + loadMethod.toString().match(/^function\s*([^\s(]+)/)[1] + ', \'' + filtros + '\')">Primeiro</a></li>');
    $('#' + grid + ' .pagination').append('    <li id="' + grid + '-page-anterior" class="disabled"><a style="cursor:pointer"'
        + ' onclick="pageAnteriorClick(\'' + grid + '\', ' + pages + ', ' + loadMethod.toString().match(/^function\s*([^\s(]+)/)[1] + ', \'' + filtros +'\')">Anterior</a></li>');

    var i = 0;
    var max = 0;

    if (pages < 9) {
        i = 1;
        max = pages;
    }  else {
        if (page <= 5) {
            max = 9;
        } else {
            max = page + 4;

            if (max > pages) {
                max = pages;
            }
        }

        i = max - 8;
    }

    for (i; i <= max; i++) {
        $('#' + grid + ' .pagination').append('    <li><a style="cursor:pointer" id="' + grid + '-page-' + i
            + '" onclick="pageClick(\'' + grid + '\', ' + i + ', ' + pages + ', ' + loadMethod.toString().match(/^function\s*([^\s(]+)/)[1] + ', \'' + filtros + '\')">' + i + '</a></li>');
    }

    $('#' + grid + ' .pagination').append('    <li id="' + grid + '-page-proximo"><a style="cursor:pointer"'
        + ' onclick="pageProximoClick(\'' + grid + '\', ' + pages + ', ' + loadMethod.toString().match(/^function\s*([^\s(]+)/)[1] + ', \'' + filtros + '\')">Próximo</a></li>');
    $('#' + grid + ' .pagination').append('    <li id="' + grid + '-page-ultimo"><a style="cursor:pointer"'
            + ' onclick="pageClick(\'' + grid + '\', ' + pages + ', ' + pages + ', ' + loadMethod.toString().match(/^function\s*([^\s(]+)/)[1] + ', \'' + filtros + '\')">Último</a></li>');
    $('#' + grid + ' .pagination').append('</ul></div>');
    $('#' + grid + '-page-' + page).addClass('selected-page');

    if (page !== 1) {
        $('#' + grid + '-page-anterior').removeClass('disabled');
        $('#' + grid + '-page-primeiro').removeClass('disabled');
    }

    if (page === pages) {
        $('#' + grid + '-page-proximo').addClass('disabled');
        $('#' + grid + '-page-ultimo').addClass('disabled');
    }
};

function pageAnteriorClick(grid, totalPages, callback, filtros) {
    var pageNumber = parseInt($('a[id*=' + grid + '-page-][class=selected-page]').text());

    if (pageNumber <= 1) {
        return;
    }

    pageClick(grid, pageNumber - 1, totalPages, callback, filtros);
};

function pageProximoClick(grid, totalPages, callback, filtros) {
    var pageNumber = parseInt($('a[id*=' + grid + '-page-][class=selected-page]').text());

    if (pageNumber >= totalPages) {
        return;
    }

    pageClick(grid, pageNumber + 1, totalPages, callback, filtros);
};

function pageClick(grid, pageNumber, totalPages, callback, filtros) {
    if($('#' + grid + '-page-' + pageNumber).hasClass('selected-page')) {
        return;
    }

    for (var i = 1; i <= totalPages; i++) {
        $('#' + grid + '-page-' + i).removeClass('selected-page');
    }

    $('#' + grid + '-page-' + pageNumber).addClass('selected-page');

    if (pageNumber === 1) {
        $('#' + grid + '-page-anterior').addClass('disabled');
    } else {
        $('#' + grid + '-page-anterior').removeClass('disabled');
    }

    if (pageNumber === totalPages) {
        $('#' + grid + '-page-proximo').addClass('disabled');
    } else {
        $('#' + grid + '-page-proximo').removeClass('disabled');
    }

    if (callback !== undefined) {
        callback(pageNumber, filtros);
    }
};

