import java.sql.DriverManager;
import java.sql.PreparedStatement;
import java.sql.ResultSet;

public class SecureSQL {
    public static void main(String[] args) {
        String username = "admin";  // entrada do usuário (podendo ser manipulada)
        String password = "12345";  // entrada do usuário (podendo ser manipulada)

        try {
            Connection conn = DriverManager.getConnection("jdbc:mysql://localhost:3306/mydb", "user", "password");

            // Utilizando Prepared Statement para evitar SQL Injection
            String query = "SELECT * FROM users WHERE username = ? AND password = ?";
            PreparedStatement pstmt = conn.prepareStatement(query);
            pstmt.setString(1, username);  // Parâmetro 1 é para username
            pstmt.setString(2, password);  // Parâmetro 2 é para password

            ResultSet rs = pstmt.executeQuery();

            while (rs.next()) {
                System.out.println("User found: " + rs.getString("username"));
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
